jQuery(function ($) {
    var r = Raphael('chaptersMap', 900, 600);
    r.safari();
    var _label = r.popup(50, 50, "").hide();
    attributes = {
        fill: '#6c8b2d',
        stroke: '#1e336a',
        'stroke-width': 2,
        'stroke-linejoin': 'round'
    };
    arr = new Array();
    /* para cada path de nuestra fuente svg vamos a dibujar un path del tipo Raphael */
    for (var correntPath in paths) {
        var obj = r.path(paths[correntPath].path);
        arr[obj.id] = correntPath;
        obj.attr(attributes);
        /* Al estar encima el mouse de nuestro correntPath, Cambiamos el color y se restablece cuando se deja */
        obj.hover(function () {
            this.animate({
                fill: '#cfbb50',
                stroke: '#1F131D'
            }, 300);
            bbox = this.getBBox();
            _label.attr({ text: paths[arr[this.id]].name }).update(bbox.x, bbox.y + bbox.height / 2, bbox.width).toFront().show();
        }, function () {
            this.animate({
                fill: attributes.fill,
                stroke: attributes.stroke
            }, 300);
            _label.hide();
        });
        /* Accion cuando le damos click a alguna parte de nuestro mapa */
        obj.click(function () {
            location.href = paths[arr[this.id]].url;
            traerCiudades(paths[arr[this.id]].name);
        });
    }//fin For
});



jQuery.expr[':'].raph = function 
(objNode, intStackIndex, arrProperties, arrNodeStack) { var c = objNode.getAttribute('class'); return (c && c.indexOf(arrProperties[3]) != -1); }


function traerCiudades(nombre) {

    var url = "Cobertura.aspx/ObtenerCiudades";
    var params = '{nombre:"' + nombre + '"}';
    var errMsg = "Error al obtener ciudades: ";
    var ciudades = "";

    var response = genericCallWebMethod(url, params, errMsg, false);
    $("#Ciudades").empty()
    if (response.d != null) {
        $.each(response.d, function (index, item) {
            ciudades += item.Ciudad + '\n';
            $("#Ciudades").append('<option value="' + item.Ciudad + '">' + item.Ciudad + '</option>');
        });
    }
}

function genericCallWebMethod(urlParam, params, errMsg, paramAsync) {
    var resp;

    $.ajax({
        type: "POST",
        url: urlParam,
        data: params,
        contentType: "application/json; charset=utf-8",
        dataType: "json",
        success: function (response) {
            resp = response;
            if (response.d.responseSuccess == 0) {
                alert(response.d.responseMessage);
            }
        },
        async: paramAsync,
        error: function (xhr, errorType, exception) { //Triggered if an error communicating with server  
            var errorMessage = exception || xhr.statusText; //If exception null, then default to xhr.statusText           
            alert(errMsg + errorMessage);
        }
    });

    return resp;
}
